import React, { Component } from 'react';
//import logo from './logo.svg';
import './App.css';
import images from "../images.json"

class App extends Component {

  constructor() {
    super();
    this.state = {
      images,
      score: 0,
      topScore: 0,
      imageClicked: [],
      message: "Click an image"
    }
    this.startGame = this.startGame.bind(this);
    console.log(this)
  }

  imageShuffle = (pictures) => {
    var random;
    var x;
    for (var i = pictures.length; i > 0; i--) {
      random = Math.floor(Math.random() * (i + 1));
      x = pictures[i];
      pictures[i] = pictures[random];
      pictures[random] = x;
    }
    return pictures;
  }

  restart = () => {
    this.setState({
      score: 0,
      topScore: 0,
      imageClicked: [],
      message: "Oops! You've already clicked that one"
    })
  }

  startGame = (event) => {
    this.setState({
      imageClicked: this.state.imageClicked.concat(event.target.id)
    })
    if (this.state.imageClicked.indexOf(event.target.id) === -1) {
      console.log("new click")
      var currentScore = this.state.score
      currentScore++;
      var currentTopScore = this.state.topScore
      if (currentScore > currentTopScore) {
        currentTopScore++;
      }
      this.setState({
        score: currentScore,
        topScore: currentTopScore,
        message: "Woohoo! You guessed correctly"
      })
      console.log(this.state)
    }
    else {
      console.log("already clicked")
      console.log(this.state.imageClicked, event.target.id)
      this.restart();
    }
    this.imageShuffle(images);
  }



  render() {
    return (
      <div className="App">
        <p
          // className={this.state.message === "You already guessed that one" ? "animated flash wrong" : ""}
        >{this.state.message}</p>
        <p>Score: {this.state.score}</p>
        <p>Top Score: {this.state.topScore}</p>
        <div id="images">
          {images.map(image => (
            <img
              src={image.url}
              id={image.id}
              alt={image.name}
              key={image.id}
              width="175px"
              height="175px"
              onClick={this.startGame}
            />

          ))}
        </div>
      </div>
    );
  }
}

export default App;
